<div class="mod mod-side mod-itemlist">    <h1 class="h-side h-box">Magic Variants</h1>    <?php        try {            if (!empty($this->db_variants)) {                foreach($this->db_variants as $row) {                    echo "                        <a class='link-block' href='/loot?item={$row['urlname']}'>                            <div class='node node-item'>                                <img class='img-itemthumb' src='/img/lena32.png'>                                <p>{$row['name']}</p>                                <p class='text-info'>Level {$row['level']} {$row['class']}</p>                            </div>                        </a>                    ";                }            } else {                throw new Exception("Nothing Magical Found");            }        } catch (Exception $e) {            echo "<p class='mod mod-notify mod-warning'>" . $e->getMessage(), "</p>\n";        }    ?></div> 